import React, { useEffect } from 'react'
import Logo from '../img/MEME.gif'

function Templates({ userInfo, templates, setTemplates, template, setTemplate, setCurrentPage }) {
    useEffect(() => {
        fetch("https://api.imgflip.com/get_memes").then(x =>
            x.json().then(response => setTemplates(response.data.memes))
        );
    }, []);

    return (
        <div>
            <div className="container-fluid bg-success">

                    <div className="row bg-primary text-white">
                        <h1 className="logo py-4 text-center col-lg-9 col-sm-12" style={{fontSize:200}}>MEMEÂ· oir</h1>
                        <div className="col-lg-3 col-sm-12 text-center">
                        <img className="img" src={Logo} alt="logo" style={{height:200, width:200}} />
                        </div>
                    </div>
                    <div className="row text-center">
                    <div className="col-lg-12 col-sm-12 bg-success">
                        <img onClick={() => setCurrentPage('profile')} className="prof img-fluid" src={userInfo.profile_img} style={{ height: 100 }} />
                        <p>{userInfo.name}</p>
                    </div>
                </div>

                <div className="col-12 bg-success mx-auto">
                    <h1 className="logo bg-primary text-center text-white mx-0 py-2">Choose Template</h1>
                    <div className="row">
                        <div className="card-columns text-center">
                            {templates.map(template => {
                                return (
                                    <div className="card bg-success">
                                        <img
                                            className="img-fluid"
                                            key={template.id}
                                            src={template.url}
                                            alt={template.name}
                                            onClick={() => {
                                                setTemplate(template);
                                                setCurrentPage('generator')
                                            }} />
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Templates
