import React, { useEffect } from 'react'

function Templates({ userInfo, templates, setTemplates }) {
    useEffect(() => {
        fetch("https://api.imgflip.com/get_memes").then(x =>
            x.json().then(response => setTemplates(response.data.memes))
        );
    }, []);

    return (
        <div>
            <div className="container-fluid bg-success">
                <div className="row">
                <h1 className="py-4 text-center col-9">MayMay</h1>
                    <div className="col-3 bg-success">
                        <img className="img-fluid py-2" src={userInfo.profile_img} style={{ height: 100 }} />
                        <p>{userInfo.name}</p>
                    </div>
                </div>
                <div className="row mx-auto text-center">
                    <div className="col-12 bg-success mx-auto">
                        <h1 className="bg-primary text-white mx-0">Choose Template</h1>
                        <div className="row">
                            <div className="card-columns">
                                {templates.map(template => {
                                    return (
                                        <div className="card">
                                            <img className="img-fluid" key={template.id} src={template.url} alt={template.name} />
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Templates
