import React from 'react';
import axios from 'axios';

function Meme({meme, userInfo, setCurrentPage}) {
    const userId = userInfo.id
    function archiveMeme(event){
    event.preventDefault();
    const url = 'http://localhost:8000/store'
    const method = 'post'


    const body ={users_id:userId, meme_url:meme}
    const data ={users_id:userId, meme_url:meme}
    axios({
        url,
        method,
        body,
        data
    })
    .then(setCurrentPage('profile'))
    .catch(err => console.log('error: ', err))
} 


    return (
        <div>
        <div className="row bg-primary text-white">
                    <h1 className="logo py-4 text-center col-lg-9 col-sm-12" style={{ fontSize: 150 }}>MEMEÂ· oir</h1>
                    <div className="col-lg-3 col-sm-12 text-center">
                        <img className="img py-2 my-2" src={Logo} alt="logo" style={{ height: 200, width: 200 }} />
                    </div>
                </div>
                <div className="row text-center">
                    <div className="col-lg-12 col-sm-12 bg-success">
                        <img onClick={() => setCurrentPage('profile')} className="prof img-fluid my-2" src={userInfo.profile_img} style={{ height: 100 }} />
                        <p>{userInfo.name}</p>
                    </div>
                </div>
            <img src={meme} alt=""/>
            <button onClick={(e)=>archiveMeme(e)}>Save</button>
        </div>
    )
}

export default Meme
