import React from 'react'
import MayMay from '../img/maymay.png'
import axios from 'axios';

function login({ bearer, setBearer, setCurrentPage, setUserEmail, userEmail, setUserPassword, userPassword }) {
    function handleSubmit(event) {
        event.preventDefault();
        const headers = {
            Accept: "application/json",
            'Content-Type': 'application/json;charset=UTF-8',
        }
        // const userHeaders = {
        //     Accept: "application/json",
        //     Authorization: `Bearer ${bearer}`
        // }
        axios({
            url: "http://localhost:8000/v1/oauth/token",
            method: "post" ,
            data:{
                grant_type:"password",
                client_id:"2",
                client_secret:"kRZzELjTaLhjjumF6BIzOdXDgNP4bl2zeqxJRysZ",
                password:userPassword,
                username: userEmail,
                scope:""
            },
            headers,
        })
            .then(res => setBearer(prevBearer=> prevBearer = res.data.access_token))
            .catch(err => console.log('error: ', err))
        console.log(bearer)
    //     axios({
    //         url: "http://localhost:8000/api/user",
    //         method: "get",
    //         userHeaders,
    //     })
    //     .then(response => console.log(response))
    //     .catch(err => console.log('error: ', err))
     }

    return (
        <div>
            <div className="container border bg-success py-4">
                <div className="row mx-auto">
                    <div className="col-lg-6 col-md-4 text-center py-4">
                        <img src={MayMay} className="img-fluid" />
                    </div>
                    <div className="col-lg-6 col-md-8 col-sm-10 mx-auto">
                        <div className="container border text-center text-primary">
                            <h1 className="py-4">MayMay</h1>
                            <form onSubmit={e => handleSubmit(e)}>
                                <label htmlFor="email" className="py-4"><b>Email</b></label><br />
                                <input
                                    type="email"
                                    className="form-control"
                                    aria-describedby="emailHelp"
                                    placeholder="Enter email"
                                    onChange={e => setUserEmail(e.target.value)}
                                />
                                <br />
                                <label htmlFor="password" className="py-4"><b>Password</b></label><br />
                                <input
                                    type="password"
                                    className="form-control"
                                    placeholder="password"
                                    onChange={e => setUserPassword(e.target.value)}
                                />
                                <br />
                                <button className="my-4 btn btn-primary text-white">Submit</button><br />
                            </form>
                            <button className="my-4 btn btn-primary text-white" onClick={() => setCurrentPage('register')}>Register Account</button>
                            <button onClick={()=>setCurrentPage('feed')}>Test Feed</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default login
