import React, {useEffect} from 'react'
import Login from './login';

function Generator({template, userInfo, setCaptions, captions}) {
    const updateCaption = (e, index) => {
        const text= e.target.value || '';
        setCaptions(
            captions.map((c, i) =>{
                if(index === i){
                    return text;
                } else {
                    return c;
                }
            })
        )
    }
    useEffect(()=>{
        setCaptions(Array(template.box_count).fill(''));
    },[template])
    return (
        <div>
            <div className="container-fluid bg-success">
                <div className="row">
                    <h1 className="py-4 text-center col-9">MayMay</h1>
                    <div className="col-3 bg-success">
                        <img className="img-fluid py-2" src={userInfo.profile_img} style={{ height: 100 }} />
                        <p>{userInfo.name}</p>
                    </div>
                </div>
                <div className="container">
                {
                    captions.map((c, index) =>(
                        <input 
                        key={index} 
                        type="text"
                        onChange={e=> updateCaption(e, index)}    
                        />
                    ))
                }
                <img className="img-fluid py-4" src={template.url} alt="template"/>
                </div>
            </div>
        </div>
    )
}

export default Generator
