import React, { useEffect } from 'react'

function Templates({ userInfo, templates, setTemplates }) {
    useEffect(() => {
        fetch("https://api.imgflip.com/get_memes").then(x =>
            x.json().then(response => setTemplates(response.data.memes))
        );
    }, []);

    return (
        <div>
            <div className="container-fluid bg-success text-center">
                <h1 className="py-4">MayMay</h1>

                <div className="row">
                    <div className="col-2 bg-success">
                        <div className="card bg-danger">
                            <img className="card-img-top" src={userInfo.profile_img} />
                            <div className="card-body">
                                <p>{userInfo.name}</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-10 bg-success">
                        <h1 className="bg-primary text-white">Choose Template</h1>
                        {templates.map(template => {
                            return (
                                <div className="row">
                                <div className="card col-4">
                                <img className="img-fluid" key={template.id} src={template.url} alt={template.name} />
                                </div>
                                </div>
                            )
                        })}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Templates
