import React, {useEffect} from 'react'
import Login from './login';

function Generator({template, userInfo, setCaptions, captions, meme, setMeme, setCurrentPage}) {
    const updateCaption = (e, index) => {
        const text= e.target.value || '';
        setCaptions(
            captions.map((c, i) =>{
                if(index === i){
                    return text;
                } else {
                    return c;
                }
            })
        )
    }
    useEffect(()=>{
        setCaptions(Array(template.box_count).fill(''));
    },[template])

    const generateMeme = () =>{
        const formData = new FormData();
        formData.append('username', 'rmke223');
        formData.append('password', 'marley123');
        formData.append('template_id', template.id);
        captions.forEach((c, index)=> formData.append(`boxes[${index}][text]`, c));

        fetch('https://api.imgflip.com/caption_image', {
            method: 'Post',
            body: formData
        }).then(res=> {
            res.json().then(res => {
                setMeme(prevmeme=> prevmeme = res.data.url)
                setCurrentPage(prevpage=> prevpage = 'meme')
            })
        });
    }

    return (
        <div>
            <div className="container-fluid bg-success">
                <div className="row text-center">
                    <h1 className="logo py-4 text-center col-lg-9 col-sm-12">M E M E Â· o i r</h1>
                    <div className="col-3 col-sm-12 bg-success">
                        <img className="prof img-fluid my-2" src={userInfo.profile_img} style={{ height: 100 }} />
                        <p>{userInfo.name}</p>
                    </div>
                </div>
                <div className="container">
                {
                    captions.map((c, index) =>(
                        <input 
                        key={index} 
                        type="text"
                        onChange={e=> updateCaption(e, index)}
                        placeHolder={`input # ${index+1}`}    
                        />
                    ))
                }
                <button onClick={generateMeme}>Generate Meme!</button>
                <img className="img-fluid py-4" src={template.url} alt="template"/>
                </div>
            </div>
        </div>
    )
}

export default Generator
